public class TestThread implements Runnable {
    private int num = 5;
    private Integer  s1 = 0;
    @Override
    public  void run(){
        while(true) {
            method();
        }
    }
    public synchronized void method(){
        if(num > 0) {
            try {
                Thread.sleep(100);
            } catch (InterruptedException e) {
                e.printStackTrace();
            }
            System.out.println(num + " " + Thread.currentThread().getName());
            num--;
        }
    }
}

